package HackerRank;

import java.io.*;
import java.util.*;

public class SherlokAndBeast {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in=new Scanner(System.in);
		
		int t,n;
		t=in.nextInt();
		for(int i=1;i<=t;i++){
			n=in.nextInt();
			if(n==1)
				System.out.println("-1");
			if(n==3)
				System.out.println("555");
			if(n==5)
				System.out.println("33333");
			
			
			
			
		}

	}

}
